import { message } from 'antd'
import axios from 'axios'
async function ajax(url,data={},type='get'){
    try{
       return axios({method: type,url,data})
       
    }catch(error){
        message.error('Something is wrong!',error.message)
    }
}

export const reqLogin  = data => ajax('api/login',data,'post')
