import { useState } from 'react';
import { Table, Space, Popconfirm, message } from 'antd';

import { useComponentDidMount } from '../../../../utils/hooks';
import { reqEvents, reqDeleteEvent } from '../../../../ajax';
import LinkButton from '../../../../components/LinkButton';



export default function EventsList(props) {
    // function onChange(pagination, filters, sorter, extra) {
    //     // console.log('params', pagination, filters, sorter, extra);
    // }
    const [eventsData, setEventsData] = useState([])
    const [loading, setLoading] = useState(true)
    const getResponseFunc = (reqFunc,setStateFunc, data=null )=>{
       return( async ()=>{
        const result = await reqFunc(data)
        // const result = response.data
        setLoading(true)
        if (result.status === 1) {
            setLoading(false)
            setStateFunc(result.data)
        } else {
            setLoading(false)
            message.error(result.msg)
        }
    }) 
    }
    const getData = getResponseFunc(reqEvents, setEventsData)
    // const getData = async () => {
    //     const result = await reqEvents()
    //     // const result = response.data
    //     setLoading(true)
    //     if (result.status === 1) {
    //         setLoading(false)
    //         setEventsData(result.data)
    //     } else {
    //         setLoading(false)
    //         message.error(result.msg)
    //     }
    // };
    useComponentDidMount(getData)

    const columns = [
        {
            title: 'Title',
            dataIndex: 'title',
            width: '15%',
            ellipsis: true,
            filters: [
                {
                    text: 'test1',
                    value: 'test1',
                },
                {
                    text: 'test2',
                    value: 'test2',
                },

            ],
            onFilter: (value, record) => record.title.indexOf(value) !== -1,
        },
        {
            title: 'Topic',
            dataIndex: 'topic',
            ellipsis: true,
            //   defaultSortOrder: 'descend',
            //   sorter: (a, b) => a.age - b.age,
            filters: [
                {
                    text: 'test1',
                    value: 'test1',
                },
                {
                    text: 'test1',
                    value: 'test2',
                },
            ],
            onFilter: (value, record) => record.address.indexOf(value) !== -1,

        },
        {
            title: 'Time',
            dataIndex: 'time',
            width: '15%',
            ellipsis: true,
            defaultSortOrder: 'descend',
            sorter: (a, b) => {
                const getTime = obj => obj.time.replace(/\D/g, "") * 1
                return getTime(a) - getTime(b)
            },
        },
        {
            title: 'Location',
            dataIndex: 'location',
            width: '15%',
            ellipsis: true,
            filters: [
                {
                    text: 'test1',
                    value: 'test1',
                },
                {
                    text: 'test1',
                    value: 'test2',
                },
            ],
            onFilter: (value, record) => record.address.indexOf(value) !== -1,

        },
        {
            title: 'Note',
            dataIndex: 'note',
            ellipsis: true,
            width: 'auto'
        },
        {
            title: 'Action',
            dataIndex: 'action',
            width: '15%',
            ellipsis: true,
            render: (text, record) => {
                const handleEdit = () => {
                    console.log('record', record)
                }
                const handleDelete = async (key) => {
                    
                    console.log(key._id)
                    const result = await reqDeleteEvent(key._id)
                    setLoading(true)
                    if (result.status === 1) {
                        setLoading(false)
                        setEventsData(result.data)
                    } else {
                        setLoading(false)
                        message.error(result.msg)
                    }

                    // const dataSource = [dataSource];
                    // this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
                    console.log(key)
                };
                return (
                    <Space size="middle">
                        <LinkButton onClick={handleEdit} >Edit {record._id}</LinkButton>
                        <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record)}>
                            <LinkButton >Delete</LinkButton>
                        </Popconfirm>
                    </Space>
                )


            }
        },
    ];




    return (
        <Table
            columns={columns}
            dataSource={eventsData}
            // onChange={onChange}
            rowKey="_id"
            loading={loading}
            pagination={{
                defaultPageSize: 12, total: eventsData.length,
                showTotal: (total, range) => `${range[0]}-${range[1]} of ${total} items`
            }}
        />
    )
}
