import { Form, Input, TimePicker,Button,DatePicker } from 'antd';
import moment from 'moment';
import {reqUpdateEvent} from '../../../../ajax'
import { useComponentDidMount } from '../../../../utils/hooks';

export default function EventForm(props){
    useComponentDidMount
    const [form] = Form.useForm();
    form.setFieldsValue(props)
    const layout = {
        labelCol: {
          span: 6,
        },
        wrapperCol: {
          span: 12,
        },
      };
      /* eslint-disable no-template-curly-in-string */
      
      const validateMessages = {
        required: '${label} is required!'
      };
      const onFinish = async (values) => {
          const dateStr = values.event.date.format('DD/MM/YYYY')
          values.event.date = dateStr
          const result = await reqUpdateEvent(values.event)
          if (result.status === 1) {
        } else {
        }
      };
      const onReset = () => {
        form.resetFields();
        
      };
      const dateFormatList = ['DD/MM/YYYY', 'DD/MM/YY'];
    return(
        <Form {...layout} form={form} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}
          >
            <Form.Item
          name={['event', '_id']}
            hidden = {true}
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={['event', 'title']}
          label="Title"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={['event', 'topic']}
          label="Topic"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={['event', 'date']}
          label="Date"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <DatePicker format={dateFormatList}/>
        </Form.Item>


        <Form.Item
          name={['event', 'location']}
          label="Locaton"
          rules={[
            {
             required:true,
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item name={['event', 'note']} label="Note">
          <Input.TextArea rows="6" />
        </Form.Item>
        <Form.Item wrapperCol={{ span:8, offset: 8 }}  >
          <Button type="primary" htmlType="submit" style={{width:"40%"}}>
            Submit
          </Button>
          <Button type="primary"  style={{width:"40%",marginLeft:"20%"}} onClick={onReset}>
              Reset
          </Button>
        </Form.Item>
      </Form>
    )
}
