import axios from 'axios'
function ajax(url,data={},type='GET'){
    if(type==='GET'){
        return axios.get(url,{
            params:data
        })
    }else if(type==='POST'){
        return (
            axios({
                method: 'post',
                url: '/api/login',
                data: {
                  username: 'Fred',
                  password: 'Flintstone'
                }
              })
        )
    }
}
export const reqLogin  = data => ajax('api/login',{username:'11111',password:'2222222'},'POST')
