import { Table,Space,Popconfirm } from 'antd';

import LinkButton from '../../../../components/LinkButton';

const columns = [
    {
      title: 'Title',
      dataIndex: 'title',
      width:'15%',
      ellipsis: true,
      filters: [
        {
          text: 'test1',
          value: 'test1',
        },
        {
          text: 'test2',
          value: 'test2',
        },
        // {
        //     text: 'Submenu',
        //     value: 'Submenu',
        //     children: [
        //       {
        //         text: '0',
        //         value: '0',
        //       },
        //       {
        //         text: '2',
        //         value: '2',
        //       },
        //     ],
        //   },
        
      ],
      // specify the condition of filtering result
      // here is that finding the name started with `value`
      onFilter: (value, record) =>record.title.indexOf(value) !== -1,
      
    //   sorter: (a, b) => a.title.length - b.title.length,
    //   sortDirections: ['descend'],
    },
    {
      title: 'Topic',
      dataIndex: 'topic',
      ellipsis: true,
    //   defaultSortOrder: 'descend',
    //   sorter: (a, b) => a.age - b.age,
          filters: [
        {
          text: 'test1',
          value: 'test1',
        },
        {
          text: 'test1',
          value: 'test2',
        },
      ],
      onFilter: (value, record) => record.address.indexOf(value) !== -1,

    },
    {
      title: 'Time',
      dataIndex: 'Time',
      width:'15%',
      ellipsis: true,
         defaultSortOrder: 'descend',
        //  a.time - b.time
        sorter: (a, b) => {
            console.log(a)
            const getTime = str =>str.Time.replace(/\D/g,"")*1
           let timeA = a.Time.replace(/\D/g,"")*1
           let timeB = b.Time.replace(/\D/g,"")*1
           return timeA-timeB
        },
    },
    {
        title: 'Location',
        dataIndex: 'location',
        width:'15%',
        ellipsis: true,
        filters: [
            {
              text: 'test1',
              value: 'test1',
            },
            {
              text: 'test1',
              value: 'test2',
            },
          ],
          onFilter: (value, record) => record.address.indexOf(value) !== -1,

    },
    {
        title: 'Note',
        dataIndex: 'note',
        ellipsis: true,
    },
    {
        title: 'Action',
        dataIndex: 'action',
        width:'15%',
        ellipsis: true,
        render: (text, record) => {
            const handleEdit =()=>{
                console.log('record',record)
            }
            const handleDelete = (key) => {
                // const dataSource = [dataSource];
                // this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
                console.log(key)
              };
            return(
            <Space size="middle">
              <LinkButton onClick={handleEdit} >Edit {record._id}</LinkButton>
              <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record)}>
              <LinkButton >Delete</LinkButton>
              </Popconfirm>
            </Space>
            )
            

        }
    },
  ];

  const data = []
  for(let i=0;i<50;i++){
    data.push(
        {
            _id: `${i}`,
            title: `John Brown ${i}` ,
            topic: `Uni visit ${i}`,
            Time: `2021-6-${i+1}`,
            location:'lincoln uni',
            note:'aaaa'
          }
    )
  }



export default function EventsList(props){
    function onChange(pagination, filters, sorter, extra) {
        // console.log('params', pagination, filters, sorter, extra);
      }
    return(
        <Table 
        columns={columns} 
        dataSource={data} 
        onChange={onChange} 
        rowKey="_id"
        pagination={{defaultPageSize:12,total:data.length,
            showTotal:(total, range) => `${range[0]}-${range[1]} of ${total} items`
        }}
        />
    )
}
