import logo from '../../assets/images/logo.jpeg'
import { NavLink, withRouter } from 'react-router-dom'
import './sideNav.less'
import { Menu } from 'antd';
import { MailOutlined } from '@ant-design/icons';
import navList from '../../configs/sideNavConfig'





function SideNav(props) {
  const { SubMenu } = Menu;
  let currentPath = props.location.pathname

  const handleClick = e =>{

  }

  const getNavNodes = navList =>{
   return navList.map(item=>{
      if(item.children){
        return(
          <SubMenu key={item.key} icon={<MailOutlined />} title={item.title}>
            {getNavNodes(item.children)}
        </SubMenu>
        )
      }else{
        
      }
    })
  }

  return (
    <div className="siderWrp" >
      <div className="topLogo" >
        <img src={logo} alt="children uni" />
      </div >

      <Menu
        style={{ width: "100%", backgroundColor:"transparent"}}
        selectedKeys={[currentPath]}
        defaultOpenKeys={['sub1']}
        mode="inline"
        className="menu"
        theme='dark'
        onClick={handleClick}
      >
        {
          navList.map((item)=>{
            if(item.children){
              return(
                <SubMenu key="sub1" icon={<MailOutlined />} title="Navigation One">
              <Menu.ItemGroup key="g1" title="Item 1">
                <Menu.Item key="1">Option 1</Menu.Item>
                <Menu.Item key="2">Option 2</Menu.Item>
              </Menu.ItemGroup>
              <Menu.ItemGroup key="g2" title="Item 2">
                <Menu.Item key="3">Option 3</Menu.Item>
                <Menu.Item key="4">Option 4</Menu.Item>
              </Menu.ItemGroup>
            </SubMenu>
              )
              
            }
            return (
              <Menu.Item key= {item.key} icon={<MailOutlined />} >
              <NavLink className="navItem" to= {item.path}> {item.title} </NavLink>
              </Menu.Item>
            )
          })
        }
      </Menu>



    </div>
  )
}

export default withRouter(SideNav)
