import { Table,Space,Popconfirm } from 'antd';

import LinkButton from '../../../../components/LinkButton';

const columns = [
    {
      title: 'Title',
      dataIndex: 'title',
      width:'15%',
      ellipsis: true,
      filters: [
        {
          text: '0',
          value: '0',
        },
        {
          text: '1',
          value: '1',
        },
        {
          text: 'Submenu',
          value: 'Submenu',
          children: [
            {
              text: 'Green',
              value: 'Green',
            },
            {
              text: 'Black',
              value: 'Black',
            },
          ],
        },
      ],
      // specify the condition of filtering result
      // here is that finding the name started with `value`
      onFilter: (value, record) =>{
        console.log(value)
        console.log('record',record._id,typeof(record._id))
       return record._id.indexOf(value) === 0
      },
      sorter: (a, b) => a.title.length - b.title.length,
      sortDirections: ['descend'],
    },
    {
      title: 'Topic',
      dataIndex: 'topic',
      ellipsis: true,
      defaultSortOrder: 'descend',
      sorter: (a, b) => a.age - b.age,
    },
    {
      title: 'Time',
      dataIndex: 'time',
      width:'15%',
      ellipsis: true,
    //   filters: [
    //     {
    //       text: 'London',
    //       value: 'London',
    //     },
    //     {
    //       text: 'New York',
    //       value: 'New York',
    //     },
    //   ],
    //   onFilter: (value, record) => record.address.indexOf(value) === 0,
    },
    {
        title: 'Location',
        dataIndex: 'location',
        width:'15%',
        ellipsis: true,

    },
    {
        title: 'Note',
        dataIndex: 'note',
        ellipsis: true,
    },
    {
        title: 'Action',
        dataIndex: 'action',
        width:'15%',
        ellipsis: true,
        render: (text, record) => {
            const handleEdit =()=>{
                console.log('record',record)
            }
            const handleDelete = (key) => {
                // const dataSource = [dataSource];
                // this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
                console.log(key)
              };
            return(
            <Space size="middle">
              <LinkButton onClick={handleEdit} >Edit {record._id}</LinkButton>
              <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record)}>
              <LinkButton >Delete</LinkButton>
              </Popconfirm>
            </Space>
            )
            

        }
    },
  ];

  const data = []
  for(let i=0;i<10;i++){
    data.push(
        {
            _id: `${i}`,
            title: `John Brown ${i}` ,
            topic: `Uni visit ${i}`,
            time: '2021-5-6',
            location:'lincoln uni',
            note:'aaaa'
          }
    )
  }



export default function EventsList(props){
    function onChange(pagination, filters, sorter, extra) {
        console.log('params', pagination, filters, sorter, extra);
      }
    return(
        <Table 
        columns={columns} 
        dataSource={data} 
        onChange={onChange} 
        rowKey="_id"
        />
    )
}
