import axios from 'axios'
function ajax(url,data={},type='post'){
    return(
        axios({
            method: type,
            url: url,
            data: {
              firstName: 'Fred',
              lastName: 'Flintstone'
            }
          })
    )


    if(type==='GET'){
        return axios.get(url,{
            params:data
        })
    }else if(type==='POST'){
        return axios.post(url,data)
    }
}
export const reqLogin  = data => ajax('api/login',data,'post')
