import { useState } from 'react';
import { Table, Space, Popconfirm } from 'antd';

import { useComponentDidMount } from '../../../../utils/hooks';
import { reqEvents, reqDeleteEvent } from '../../../../ajax';
import { getRespFunc } from '../../../../utils/globalFunc';
import LinkButton from '../../../../components/LinkButton';



export default function EventsList(props) {
    // function onChange(pagination, filters, sorter, extra) {
    //     // console.log('params', pagination, filters, sorter, extra);
    // }
    // declear the state eventsData to manage all the data of events
    const [eventsData, setEventsData] = useState([])
    // declear the state loading for Table to display before response get back
    const [loading, setLoading] = useState(true)
    // function used to send request and set the state eventsData 
    const getData = getRespFunc(reqEvents, setEventsData,setLoading)
    // call the function while the component did mount
    useComponentDidMount(getData)

    class Column{
        constructor(title,dataIndex, width='auto',ellipsis=true){
            this.title = title;
            this.dataIndex = da
        }
    }
    const columns = [
        {
            title: 'Title',
            dataIndex: 'title',
            width: '15%',
            ellipsis: true,
            filters: [
                {
                    text: 'test1',
                    value: 'test1',
                },
                {
                    text: 'test2',
                    value: 'test2',
                },

            ],
            onFilter: (value, record) => record.title.indexOf(value) !== -1,
        },
        {
            title: 'Topic',
            dataIndex: 'topic',
            ellipsis: true,
            filters: [
                {
                    text: 'test1',
                    value: 'test1',
                },
                {
                    text: 'test1',
                    value: 'test2',
                },
            ],
            onFilter: (value, record) => record.topic.indexOf(value) !== -1,

        },
        {
            title: 'Time',
            dataIndex: 'time',
            width: '15%',
            ellipsis: true,
            defaultSortOrder: 'descend',
            sorter: (a, b) => {
                const getTime = obj => obj.time.replace(/\D/g, "") * 1
                return getTime(a) - getTime(b)
            },
        },
        {
            title: 'Location',
            dataIndex: 'location',
            width: '15%',
            ellipsis: true,
            filters: [
                {
                    text: 'test1',
                    value: 'test1',
                },
                {
                    text: 'test1',
                    value: 'test2',
                },
            ],
            onFilter: (value, record) => record.location.indexOf(value) !== -1,

        },
        {
            title: 'Note',
            dataIndex: 'note',
            ellipsis: true,
            width: 'auto'
        },
        {
            title: 'Action',
            dataIndex: 'action',
            width: '15%',
            ellipsis: true,
            render: (text, record) => {
                const handleEdit = () => {
                    console.log('record', record)
                }
                const handleDelete = async (key) => {
                    getRespFunc(reqDeleteEvent, setEventsData,setLoading,key._id)()
                };
                return (
                    <Space size="middle">
                        <LinkButton onClick={handleEdit} >Edit {record._id}</LinkButton>
                        <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record)}>
                            <LinkButton >Delete</LinkButton>
                        </Popconfirm>
                    </Space>
                )
            }
        },
    ];

    return (
        <Table
            columns={columns}
            dataSource={eventsData}
            // onChange={onChange}
            rowKey="_id"
            loading={loading}
            pagination={{
                defaultPageSize: 12, total: eventsData.length,
                showTotal: (total, range) => `${range[0]}-${range[1]} of ${total} items`
            }}
        />
    )
}
