import React from 'react'
import logo from '../../assets/images/logo.jpeg'
import { NavLink, withRouter } from 'react-router-dom'
import './sideNav.less'
import { Menu } from 'antd';
import { MailOutlined } from '@ant-design/icons';
import navList from '../../configs/sideNavConfig'





function SideNav(props) {
  const { SubMenu } = Menu;
  let currentPath = props.location.pathname

  const handleClick = e =>{
    props.history.push(e.key)
    console.log(e.key)
  }

  let openSub = currentPath.split('/')

  const getNavNodes = navList =>{
   return navList.map(item=>{
      if(item.children){
        return(
          <SubMenu key={item.key} icon={<MailOutlined />} title={item.title}>
            {getNavNodes(item.children)}
        </SubMenu>
        )
      }else{
        return(
          <Menu.Item key={item.key}>{item.title}</Menu.Item>
        )
      }
    })
  }
  const navNotes = getNavNodes(navList)

  return (
    <div className="siderWrp" >
      <div className="topLogo" >
        <img src={logo} alt="children uni" />
      </div >

      <Menu
        style={{ width: "100%", backgroundColor:"transparent"}}
        selectedKeys={[currentPath]}
        defaultOpenKeys={[openSub]}
        mode="inline"
        className="menu"
        theme='dark'
        onClick={handleClick}
      >
        {navNotes}
      </Menu>



    </div>
  )
}

export default withRouter(SideNav)
