import{NavLink, withRouter} from 'react-router-dom';
import { Breadcrumb } from 'antd';

function TopNav(props){
    const currentPath = props.location.pathname
    console.log('currentpath',currentPath)
    const pathSnippets = currentPath.split("/").filter(i=>i)
    let pathHead = pathSnippets[0]
    const linkMap=[
        {'path':`/${pathHead}/list`,'key':`topNav/${pathHead}/list`,'title':`${pathHead} List`},
        {'path':`/${pathHead}/add`,'key':`topNav/${pathHead}/add`,'title':`Add ${pathHead}`},
        {'path':`/${pathHead}/upload`,'key':`topNav/${pathHead}/upload`,'title':`Upload ${pathHead} Spreadsheet`},
    ]
    if(pathHead!=='da'){
        return(
            <Breadcrumb>
        <Breadcrumb.Item>{pathHead}</Breadcrumb.Item>
        {
            linkMap.map(item=>{
                return(
                    <Breadcrumb.Item>
                    <NavLink to={item.path} key={item.key} > {item.title}  </NavLink>
                    </Breadcrumb.Item>
                )
            })
        }
        </Breadcrumb>
        )

    }else{
        return(
            <Breadcrumb>
        <Breadcrumb.Item>{pathHead}</Breadcrumb.Item>
        </Breadcrumb>
        )
    }

//     return(
