import { message } from 'antd'
import axios from 'axios'
function ajax(url,data={},type='get'){
     
    axios({method: type,url,data}).then(
        response=> {resolve(response)}
    )
    
}

export const reqLogin  = data => ajax('api/login',data,'post')
