import {message} from 'antd'

export const getRespFunc = (reqFunc,setStateFunc, setLoading,data=null )=>{
    return( async ()=>{
     const result = await reqFunc(data)
     // const result = response.data
     setLoading(true)
     if (result.status === 1) {
         setLoading(false)
         setStateFunc(result.data)
     } else {
         setLoading(false)
         message.error(result.msg)
     }
 }) 
 }
