import { useState } from 'react';
import { Table, Space, Popconfirm,message } from 'antd';

import { useComponentDidMount } from '../../../../utils/hooks';
import { reqEvents } from '../../../../ajax';
import LinkButton from '../../../../components/LinkButton';

const columns = [
    {
        title: 'Title',
        dataIndex: 'title',
        width: '15%',
        ellipsis: true,
        filters: [
            {
                text: 'test1',
                value: 'test1',
            },
            {
                text: 'test2',
                value: 'test2',
            },

        ],
        onFilter: (value, record) => record.title.indexOf(value) !== -1,
    },
    {
        title: 'Topic',
        dataIndex: 'topic',
        ellipsis: true,
        //   defaultSortOrder: 'descend',
        //   sorter: (a, b) => a.age - b.age,
        filters: [
            {
                text: 'test1',
                value: 'test1',
            },
            {
                text: 'test1',
                value: 'test2',
            },
        ],
        onFilter: (value, record) => record.address.indexOf(value) !== -1,

    },
    {
        title: 'Time',
        dataIndex: 'time',
        width: '15%',
        ellipsis: true,
        defaultSortOrder: 'descend',
        sorter: (a, b) => {
            const getTime = obj => obj.time.replace(/\D/g, "") * 1
            return getTime(a) - getTime(b)
        },
    },
    {
        title: 'Location',
        dataIndex: 'location',
        width: '15%',
        ellipsis: true,
        filters: [
            {
                text: 'test1',
                value: 'test1',
            },
            {
                text: 'test1',
                value: 'test2',
            },
        ],
        onFilter: (value, record) => record.address.indexOf(value) !== -1,

    },
    {
        title: 'Note',
        dataIndex: 'note',
        ellipsis: true,
    },
    {
        title: 'Action',
        dataIndex: 'action',
        width: '15%',
        ellipsis: true,
        render: (text, record) => {
            const handleEdit = () => {
                console.log('record', record)
            }
            const handleDelete = (key) => {
                // const dataSource = [dataSource];
                // this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
                console.log(key)
            };
            return (
                <Space size="middle">
                    <LinkButton onClick={handleEdit} >Edit {record._id}</LinkButton>
                    <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record)}>
                        <LinkButton >Delete</LinkButton>
                    </Popconfirm>
                </Space>
            )


        }
    },
];
let data = []

getData()

export default function EventsList(props) {
    // function onChange(pagination, filters, sorter, extra) {
    //     // console.log('params', pagination, filters, sorter, extra);
    // }
    const [eventsData, setEventsData] = useState([])
    const getData = async ()=>{
        const result = await reqEvents()
            // const result = response.data
            if (result.status === 1) {
                data = result.data
            } else {
                message.error(result.msg)
            }
        };
    
    return (
        <Table
            columns={columns}
            dataSource={eventsData}
            // onChange={onChange}
            rowKey="_id"
            pagination={{
                defaultPageSize: 12, total: data.length,
                showTotal: (total, range) => `${range[0]}-${range[1]} of ${total} items`
            }}
        />
    )
}
